import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate, Link } from 'react-router-dom';
import Dashboard from './Dashboard';
import ProductManagement from './ProductManagement';
import UserManagement from './UserManagement'; // Import UserManagement

const App = () => {
    const [isAuthenticated, setIsAuthenticated] = useState(false);
    const [products, setProducts] = useState([
        { id: 1, name: 'Chicken Wings', category: 'Food', quantity: 50, price: 10 },
        { id: 2, name: 'Soda', category: 'Beverage', quantity: 100, price: 1.5 },
    ]);
    const [users, setUsers] = useState([]); // State for users

    const handleLogin = () => setIsAuthenticated(true);
    const handleLogout = () => setIsAuthenticated(false);

    const containerStyle = {
        textAlign: 'center',
        margin: '2rem auto',
        padding: '2rem',
        maxWidth: '400px',
        borderRadius: '8px',
        boxShadow: '0 0 15px rgba(0, 0, 0, 0.2)',
        background: 'linear-gradient(135deg, #6dd5ed, #2193b0)',
        border: '2px solid #fff',
    };

    const navStyle = {
        background: '#333',
        padding: '1rem',
        display: 'flex',
        justifyContent: 'center',
    };

    const linkStyle = {
        color: '#fff',
        textDecoration: 'none',
        margin: '0 1rem',
    };

    const buttonStyle = {
        padding: '0.5rem 1rem',
        background: 'linear-gradient(to right, #ff416c, #ff4b1f)',
        color: '#fff',
        border: 'none',
        borderRadius: '4px',
        cursor: 'pointer',
        transition: 'background 0.3s',
    };

    return (
        <Router>
            <marquee style={{ background: '#333', color: '#fff', padding: '1rem' }}>Limkokwing Wings Cafe</marquee>
            <div>
                <nav style={navStyle}>
                    {isAuthenticated && (
                        <>
                            <Link to="/dashboard" style={linkStyle}>Dashboard</Link>
                            <Link to="/products" style={linkStyle}>Product Management</Link>
                            <Link to="/users" style={linkStyle}>User Management</Link> {/* New User Management Link */}
                            <Link to="/login" style={linkStyle} onClick={handleLogout}>Logout</Link>
                        </>
                    )}
                </nav>

                <Routes>
                    <Route 
                        path="/login" 
                        element={isAuthenticated ? <Navigate to="/dashboard" /> : (
                            <div style={containerStyle}>
                                <h2 style={{ color: '#fff' }}>Login</h2>
                                <form onSubmit={(e) => { e.preventDefault(); handleLogin(); }}>
                                    <input type="text" placeholder="Username" required style={{ margin: '0.5rem 0', padding: '0.5rem', width: '100%' }} />
                                    <input type="password" placeholder="Password" required style={{ margin: '0.5rem 0', padding: '0.5rem', width: '100%' }} />
                                    <button type="submit" style={buttonStyle}>
                                        Login
                                    </button>
                                </form>
                            </div>
                        )}
                    />
                    <Route 
                        path="/products" 
                        element={isAuthenticated ? <ProductManagement products={products} setProducts={setProducts} /> : <Navigate to="/login" />} 
                    />
                    <Route 
                        path="/dashboard" 
                        element={isAuthenticated ? <Dashboard products={products} /> : <Navigate to="/login" />} 
                    />
                    <Route 
                        path="/users" 
                        element={isAuthenticated ? <UserManagement users={users} setUsers={setUsers} /> : <Navigate to="/login" />} 
                    /> {/* New User Management Route */}
                    <Route path="/" element={<Navigate to="/login" />} />
                </Routes>
            </div>
        </Router>
    );
};

export default App;
