import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate, Link } from 'react-router-dom';
import Dashboard from './Dashboard';
import ProductManagement from './ProductManagement';
import User from './User';
import UserManagement from './UserManagement'; // Assuming this is still needed

const App = () => {
    const [isAuthenticated, setIsAuthenticated] = useState(false);
    const [products, setProducts] = useState([
        { id: 1, name: 'Chicken Wings', category: 'Food', quantity: 50, price: 10 },
        { id: 2, name: 'Soda', category: 'Beverage', quantity: 100, price: 1.5 },
    ]);

    const handleLogin = () => setIsAuthenticated(true);
    const handleLogout = () => setIsAuthenticated(false);

    return (
        <Router>
            <marquee>Limkokwing Wings Cafe</marquee>
            <div>
                <nav style={{ background: '#333', padding: '1rem', display: 'flex', justifyContent: 'center' }}>
                    {isAuthenticated && (
                        <>
                            <Link to="/dashboard" style={{ color: '#fff', margin: '0 1rem' }}>Dashboard</Link>
                            <Link to="/products" style={{ color: '#fff', margin: '0 1rem' }}>Product Management</Link>
                            <Link to="/users" style={{ color: '#fff', margin: '0 1rem' }}>User</Link>
                            <Link to="/login" style={{ color: '#fff', margin: '0 1rem' }} onClick={handleLogout}>Logout</Link>
                        </>
                    )}
                </nav>

                <Routes>
                    <Route path="/login" element={isAuthenticated ? <Navigate to="/products" /> : <UserManagement onLogin={handleLogin} />} />
                    <Route path="/products" element={isAuthenticated ? <ProductManagement products={products} setProducts={setProducts} /> : <Navigate to="/login" />} />
                    <Route path="/dashboard" element={isAuthenticated ? <Dashboard products={products} onLogout={handleLogout} /> : <Navigate to="/login" />} />
                    <Route path="/users" element={isAuthenticated ? <User /> : <Navigate to="/login" />} />
                    <Route path="/" element={<Navigate to="/login" />} />
                </Routes>
            </div>
        </Router>
    );
};

export default App;
