import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate, Link } from 'react-router-dom';
import Dashboard from './Dashboard';
import ProductManagement from './ProductManagement';

const App = () => {
    const [isSignedUp, setIsSignedUp] = useState(false);
    const [isAuthenticated, setIsAuthenticated] = useState(false);
    const [credentials, setCredentials] = useState({ username: '', password: '' });
    const [loginData, setLoginData] = useState({ username: '', password: '' });
    const [products, setProducts] = useState([
        { id: 1, name: 'Chicken Wings', category: 'Food', quantity: 50, price: 10 },
        { id: 2, name: 'Soda', category: 'Beverage', quantity: 100, price: 1.5 },
    ]);

    const handleSignUp = (e) => {
        e.preventDefault();
        setIsSignedUp(true);
    };

    const handleLogin = (e) => {
        e.preventDefault();
        if (loginData.username === credentials.username && loginData.password === credentials.password) {
            setIsAuthenticated(true);
        } else {
            alert("Invalid username or password.");
        }
    };

    const handleLogout = () => {
        setIsAuthenticated(false);
        setLoginData({ username: '', password: '' });
    };

    const navStyles = {
        container: {
            background: '#333',
            padding: '1rem',
            display: 'flex',
            justifyContent: 'center',
        },
        link: {
            color: '#fff',
            textDecoration: 'none',
            margin: '0 1rem',
            padding: '0.5rem 1rem',
            borderRadius: '4px',
            background: '#555',
            transition: 'background 0.3s',
            cursor: 'pointer',
        },
    };

    return (
        <Router>
            <marquee>Limkokwing Wings Cafe</marquee>
            <div>
                <nav style={navStyles.container}>
                    {isAuthenticated && (
                        <>
                            <Link to="/dashboard" style={navStyles.link}>Dashboard</Link>
                            <Link to="/products" style={navStyles.link}>Product Management</Link>
                            <Link to="/login" style={navStyles.link} onClick={handleLogout}>Logout</Link>
                        </>
                    )}
                </nav>

                <Routes>
                    <Route
                        path="/signup"
                        element={
                            isSignedUp ? (
                                <Navigate to="/login" />
                            ) : (
                                <div style={{ textAlign: 'center', marginTop: '2rem' }}>
                                    <h2>Create an Account</h2>
                                    <form onSubmit={handleSignUp}>
                                        <input
                                            type="text"
                                            placeholder="Username"
                                            value={credentials.username}
                                            onChange={(e) => setCredentials({ ...credentials, username: e.target.value })}
                                            required
                                            style={{ padding: '0.5rem', margin: '0.5rem 0' }}
                                        />
                                        <input
                                            type="password"
                                            placeholder="Password"
                                            value={credentials.password}
                                            onChange={(e) => setCredentials({ ...credentials, password: e.target.value })}
                                            required
                                            style={{ padding: '0.5rem', margin: '0.5rem 0' }}
                                        />
                                        <button
                                            type="submit"
                                            style={{
                                                padding: '0.5rem 2rem',
                                                fontSize: '1rem',
                                                color: '#fff',
                                                background: '#007bff',
                                                border: 'none',
                                                borderRadius: '5px',
                                                cursor: 'pointer',
                                            }}
                                        >
                                            Sign Up
                                        </button>
                                    </form>
                                </div>
                            )
                        }
                    />
                    <Route
                        path="/login"
                        element={
                            isAuthenticated ? (
                                <Navigate to="/products" />
                            ) : (
                                isSignedUp ? (
                                    <div style={{ textAlign: 'center', marginTop: '2rem' }}>
                                        <h2>Welcome Back!</h2>
                                        <form onSubmit={handleLogin}>
                                            <input
                                                type="text"
                                                placeholder="Username"
                                                value={loginData.username}
                                                onChange={(e) => setLoginData({ ...loginData, username: e.target.value })}
                                                required
                                                style={{ padding: '0.5rem', margin: '0.5rem 0' }}
                                            />
                                            <input
                                                type="password"
                                                placeholder="Password"
                                                value={loginData.password}
                                                onChange={(e) => setLoginData({ ...loginData, password: e.target.value })}
                                                required
                                                style={{ padding: '0.5rem', margin: '0.5rem 0' }}
                                            />
                                            <button
                                                type="submit"
                                                style={{
                                                    padding: '0.5rem 2rem',
                                                    fontSize: '1rem',
                                                    color: '#fff',
                                                    background: '#28a745',
                                                    border: 'none',
                                                    borderRadius: '5px',
                                                    cursor: 'pointer',
                                                }}
                                            >
                                                Log In
                                            </button>
                                        </form>
                                    </div>
                                ) : (
                                    <Navigate to="/signup" />
                                )
                            )
                        }
                    />
                    <Route
                        path="/products"
                        element={isAuthenticated ? <ProductManagement products={products} setProducts={setProducts} /> : <Navigate to="/login" />}
                    />
                    <Route
                        path="/dashboard"
                        element={isAuthenticated ? <Dashboard products={products} onLogout={handleLogout} /> : <Navigate to="/login" />}
                    />
                    <Route path="/" element={<Navigate to="/signup" />} />
                </Routes>
            </div>
        </Router>
    );
};

export default App;
