import React, { useState } from 'react';
import './App.css'; // Import the CSS for styling
import background from './assets/Wings2.png';

// Dashboard Component
const Dashboard = ({ products }) => {
    const totalProducts = products.length;
    const totalStock = products.reduce((sum, product) => sum + product.quantity, 0);

    return (
        <div className="dashboard">
            <h1>Dashboard</h1>
            <div className="dashboard-stats">
                <div>Total Products: {totalProducts}</div>
                <div>Total Stock: {totalStock}</div>
            </div>

            <h2>Stock Overview</h2>
            <table className="styled-table">
                <thead>
                    <tr>
                        <th>Product</th>
                        <th>Category</th>
                        <th>Stock</th>
                    </tr>
                </thead>
                <tbody>
                    {products.map((product) => (
                        <tr key={product.id}>
                            <td>{product.name}</td>
                            <td>{product.category}</td>
                            <td>{product.quantity}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

// Product Management Component
const ProductManagement = ({ products, setProducts }) => {
    const [formData, setFormData] = useState({ name: '', category: '', quantity: 0, price: 0 });

    const handleSubmit = (e) => {
        e.preventDefault();
        if (formData.id) {
            const updatedProducts = products.map((product) =>
                product.id === formData.id ? formData : product
            );
            setProducts(updatedProducts);
        } else {
            setProducts([...products, { ...formData, id: Date.now() }]);
        }
        setFormData({ name: '', category: '', quantity: 0, price: 0 });
    };

    const handleDelete = (id) => {
        setProducts(products.filter((product) => product.id !== id));
    };

    return (
        <div className="product-management">
            <h2>Product Management</h2>
            <form onSubmit={handleSubmit} className="styled-form">
                <input
                    type="text"
                    placeholder="Name"
                    value={formData.name}
                    onChange={(e) => setFormData({ ...formData, name: e.target.value })}
                    required
                />
                <input
                    type="text"
                    placeholder="Category"
                    value={formData.category}
                    onChange={(e) => setFormData({ ...formData, category: e.target.value })}
                    required
                />
                <input
                    type="number"
                    placeholder="Quantity"
                    value={formData.quantity}
                    onChange={(e) => setFormData({ ...formData, quantity: parseInt(e.target.value) })}
                    required
                />
                <input
                    type="number"
                    placeholder="Price"
                    value={formData.price}
                    onChange={(e) => setFormData({ ...formData, price: parseFloat(e.target.value) })}
                    required
                />
                <button type="submit" className="form-button">
                    {formData.id ? 'Update' : 'Add'} Product
                </button>
            </form>

            <ul className="product-list">
                {products.map((product) => (
                    <li key={product.id}>
                        {product.name} - {product.category} - {product.quantity} in stock
                        <button onClick={() => setFormData(product)} className="edit-button">Edit</button>
                        <button onClick={() => handleDelete(product.id)} className="delete-button">Delete</button>
                    </li>
                ))}
            </ul>
        </div>
    );
};

// User Management Component
const UserManagement = ({ users, setUsers }) => {
    const [userData, setUserData] = useState({ username: '', email: '', role: '' });

    const handleSubmit = (e) => {
        e.preventDefault();
        if (userData.id) {
            const updatedUsers = users.map((user) =>
                user.id === userData.id ? userData : user
            );
            setUsers(updatedUsers);
        } else {
            setUsers([...users, { ...userData, id: Date.now() }]);
        }
        setUserData({ username: '', email: '', role: '' });
    };

    const handleDelete = (id) => {
        setUsers(users.filter((user) => user.id !== id));
    };

    return (
        <div className="user-management">
            <h2>User Management</h2>
            <form onSubmit={handleSubmit} className="styled-form">
                <input
                    type="text"
                    placeholder="Username"
                    value={userData.username}
                    onChange={(e) => setUserData({ ...userData, username: e.target.value })}
                    required
                />
                <input
                    type="email"
                    placeholder="Email"
                    value={userData.email}
                    onChange={(e) => setUserData({ ...userData, email: e.target.value })}
                    required
                />
                <input
                    type="text"
                    placeholder="Role"
                    value={userData.role}
                    onChange={(e) => setUserData({ ...userData, role: e.target.value })}
                    required
                />
                <button type="submit" className="form-button">
                    {userData.id ? 'Update' : 'Add'} User
                </button>
            </form>

            <ul className="user-list">
                {users.map((user) => (
                    <li key={user.id}>
                        {user.username} - {user.email} - {user.role}
                        <button onClick={() => setUserData(user)} className="edit-button">Edit</button>
                        <button onClick={() => handleDelete(user.id)} className="delete-button">Delete</button>
                    </li>
                ))}
            </ul>
        </div>
    );
};

// Main App Component
const App = () => {
    const [view, setView] = useState('dashboard');
    const [products, setProducts] = useState([
        { id: 1, name: 'Chicken Wings', category: 'Food', quantity: 50, price: 10 },
        { id: 2, name: 'Soda', category: 'Beverage', quantity: 100, price: 1.5 },
    ]);
    const [users, setUsers] = useState([
        { id: 1, username: 'admin', email: 'admin@example.com', role: 'Admin' },
        { id: 2, username: 'staff', email: 'staff@example.com', role: 'Staff' },
    ]);

    return (
        <div>
            <nav className="navigation">
                <button onClick={() => setView('dashboard')} className="nav-button">Dashboard</button>
                <button onClick={() => setView('products')} className="nav-button">Product Management</button>
                <button onClick={() => setView('users')} className="nav-button">User Management</button>
            </nav>

            <div className="main-view">
                {view === 'dashboard' && <Dashboard products={products} />}
                {view === 'products' && <ProductManagement products={products} setProducts={setProducts} />}
                {view === 'users' && <UserManagement users={users} setUsers={setUsers} />}
            </div>
        </div>
    );
};

export default App;
