// src/components/Dashboard.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Dashboard = () => {
  const [products, setProducts] = useState([]);

  // Fetch products from the database on component mount
  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await axios.get('http://localhost:5001/api/products');
        setProducts(response.data);
      } catch (error) {
        console.error('Error fetching products:', error);
      }
    };
    fetchProducts();
  }, []);

  // Function to handle adding a new product
  const addProduct = async (newProduct) => {
    try {
      const response = await axios.post('http://localhost:5001/api/products', newProduct);
      setProducts([...products, { ...newProduct, id: response.data.id }]); // Update UI with new product
    } catch (error) {
      console.error('Error adding product:', error);
    }
  };

  return (
    <div>
      <h2>Product Dashboard</h2>
      <ProductTable products={products} />
      <ProductForm onAddProduct={addProduct} />
    </div>
  );
};

// Table Component for displaying products
const ProductTable = ({ products }) => (
  <table>
    <thead>
      <tr>
        <th>ID</th>
        <th>Name</th>
        <th>Description</th>
        <th>Category</th>
        <th>Price</th>
        <th>Quantity</th>
      </tr>
    </thead>
    <tbody>
      {products.map((product) => (
        <tr key={product.id}>
          <td>{product.id}</td>
          <td>{product.name}</td>
          <td>{product.description}</td>
          <td>{product.category}</td>
          <td>${product.price.toFixed(2)}</td>
          <td>{product.quantity}</td>
        </tr>
      ))}
    </tbody>
  </table>
);

// Product form component for adding a new product
const ProductForm = ({ onAddProduct }) => {
  const [formData, setFormData] = useState({
    name: '',
    description: '',
    category: '',
    price: 0,
    quantity: 0
  });

  const handleSubmit = (e) => {
    e.preventDefault();
    onAddProduct(formData); // Call addProduct function in the Dashboard component
    setFormData({ name: '', description: '', category: '', price: 0, quantity: 0 }); // Reset form
  };

  return (
    <form onSubmit={handleSubmit}>
      <input
        type="text"
        placeholder="Name"
        value={formData.name}
        onChange={(e) => setFormData({ ...formData, name: e.target.value })}
        required
      />
      <input
        type="text"
        placeholder="Description"
        value={formData.description}
        onChange={(e) => setFormData({ ...formData, description: e.target.value })}
        required
      />
      <input
        type="text"
        placeholder="Category"
        value={formData.category}
        onChange={(e) => setFormData({ ...formData, category: e.target.value })}
        required
      />
      <input
        type="number"
        placeholder="Price"
        value={formData.price}
        onChange={(e) => setFormData({ ...formData, price: parseFloat(e.target.value) })}
        required
      />
      <input
        type="number"
        placeholder="Quantity"
        value={formData.quantity}
        onChange={(e) => setFormData({ ...formData, quantity: parseInt(e.target.value) })}
        required
      />
      <button type="submit">Add Product</button>
    </form>
  );
};

export default Dashboard;
