import React from 'react';

const Dashboard = ({ products, onLogout }) => {
    const totalProducts = products.length;
    const totalStock = products.reduce((sum, product) => sum + product.quantity, 0);

    // Styling objects
    const dashboardStyle = {
        padding: '2rem',
        backgroundColor: '#f5f5f5',
        color: '#333',
        borderRadius: '8px',
        boxShadow: '0 4px 12px rgba(0, 0, 0, 0.15)',
        maxWidth: '800px',
        margin: '0 auto',
        textAlign: 'center',
    };

    const buttonStyle = {
        backgroundColor: '#ff5e57',
        color: '#fff',
        border: 'none',
        borderRadius: '5px',
        padding: '0.6rem 1.2rem',
        fontSize: '1rem',
        cursor: 'pointer',
        marginBottom: '1rem',
        transition: 'background-color 0.3s',
    };

    const statsStyle = {
        display: 'flex',
        justifyContent: 'space-around',
        padding: '1rem 0',
        fontWeight: 'bold',
        fontSize: '1.2rem',
    };

    const tableStyle = {
        width: '100%',
        borderCollapse: 'collapse',
        marginTop: '1.5rem',
    };

    const thStyle = {
        backgroundColor: '#4CAF50',
        color: '#fff',
        padding: '0.8rem',
        fontSize: '1rem',
    };

    const tdStyle = {
        padding: '0.8rem',
        borderBottom: '1px solid #ddd',
        fontSize: '0.9rem',
        textAlign: 'center',
    };

    const rowStyle = {
        backgroundColor: '#f9f9f9',
    };

    const rowAltStyle = {
        backgroundColor: '#e8f5e9',
    };

    return (
        <div style={dashboardStyle}>
            <h1>Dashboard</h1>
            <button onClick={onLogout} style={buttonStyle}>Logout</button>
            <div style={statsStyle}>
                <div>Total Products: {totalProducts}</div>
                <div>Total Stock: {totalStock}</div>
            </div>

            <h2>Stock Overview</h2>
            <table style={tableStyle}>
                <thead>
                    <tr>
                        <th style={thStyle}>Product</th>
                        <th style={thStyle}>Category</th>
                        <th style={thStyle}>Stock</th>
                    </tr>
                </thead>
                <tbody>
                    {products.map((product, index) => (
                        <tr key={product.id} style={index % 2 === 0 ? rowStyle : rowAltStyle}>
                            <td style={tdStyle}>{product.name}</td>
                            <td style={tdStyle}>{product.category}</td>
                            <td style={tdStyle}>{product.quantity}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default Dashboard;
