import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate, Link } from 'react-router-dom';
import Dashboard from './Dashboard';
import ProductManagement from './ProductManagement';

const App = () => {
    const [isAuthenticated, setIsAuthenticated] = useState(false);
    const [users, setUsers] = useState([]);
    const [loginData, setLoginData] = useState({ username: '', password: '' });

    const handleSignUp = (e, newUser) => {
        e.preventDefault();
        setUsers([...users, newUser]);
        alert("Sign Up Successful! You can now log in.");
    };

    const handleLogin = (e) => {
        e.preventDefault();
        const userExists = users.find(
            (user) => user.username === loginData.username && user.password === loginData.password
        );
        if (userExists) {
            setIsAuthenticated(true);
            setLoginData({ username: '', password: '' });
        } else {
            alert("Invalid username or password.");
        }
    };

    const handleLogout = () => {
        setIsAuthenticated(false);
    };

    const navStyles = {
        container: {
            background: '#333',
            padding: '1rem',
            display: 'flex',
            justifyContent: 'center',
        },
        link: {
            color: '#fff',
            textDecoration: 'none',
            margin: '0 1rem',
            padding: '0.5rem 1rem',
            borderRadius: '4px',
            background: '#555',
            transition: 'background 0.3s',
            cursor: 'pointer',
        },
    };

    return (
        <Router>
            <marquee>Limkokwing Wings Cafe</marquee>
            <div>
                <nav style={navStyles.container}>
                    {isAuthenticated && (
                        <>
                            <Link to="/dashboard" style={navStyles.link}>Dashboard</Link>
                            <Link to="/products" style={navStyles.link}>Product Management</Link>
                            <Link to="/login" style={navStyles.link} onClick={handleLogout}>Logout</Link>
                        </>
                    )}
                </nav>

                <Routes>
                    <Route
                        path="/auth"
                        element={
                            isAuthenticated ? (
                                <Navigate to="/dashboard" />
                            ) : (
                                <div style={{ textAlign: 'center', marginTop: '2rem' }}>
                                    <h2>Welcome to Limkokwing Wings Cafe</h2>
                                    <div style={{ display: 'flex', justifyContent: 'center', gap: '2rem', marginTop: '2rem' }}>
                                        {/* Sign Up Form */}
                                        <div>
                                            <h3>Sign Up</h3>
                                            <form onSubmit={(e) => handleSignUp(e, loginData)}>
                                                <input
                                                    type="text"
                                                    placeholder="Username"
                                                    value={loginData.username}
                                                    onChange={(e) => setLoginData({ ...loginData, username: e.target.value })}
                                                    required
                                                    style={{ padding: '0.5rem', margin: '0.5rem 0' }}
                                                />
                                                <input
                                                    type="password"
                                                    placeholder="Password"
                                                    value={loginData.password}
                                                    onChange={(e) => setLoginData({ ...loginData, password: e.target.value })}
                                                    required
                                                    style={{ padding: '0.5rem', margin: '0.5rem 0' }}
                                                />
                                                <button
                                                    type="submit"
                                                    style={{
                                                        padding: '0.5rem 2rem',
                                                        fontSize: '1rem',
                                                        color: '#fff',
                                                        background: '#007bff',
                                                        border: 'none',
                                                        borderRadius: '5px',
                                                        cursor: 'pointer',
                                                    }}
                                                >
                                                    Sign Up
                                                </button>
                                            </form>
                                        </div>

                                        {/* Log In Form */}
                                        <div>
                                            <h3>Log In</h3>
                                            <form onSubmit={handleLogin}>
                                                <input
                                                    type="text"
                                                    placeholder="Username"
                                                    value={loginData.username}
                                                    onChange={(e) => setLoginData({ ...loginData, username: e.target.value })}
                                                    required
                                                    style={{ padding: '0.5rem', margin: '0.5rem 0' }}
                                                />
                                                <input
                                                    type="password"
                                                    placeholder="Password"
                                                    value={loginData.password}
                                                    onChange={(e) => setLoginData({ ...loginData, password: e.target.value })}
                                                    required
                                                    style={{ padding: '0.5rem', margin: '0.5rem 0' }}
                                                />
                                                <button
                                                    type="submit"
                                                    style={{
                                                        padding: '0.5rem 2rem',
                                                        fontSize: '1rem',
                                                        color: '#fff',
                                                        background: '#28a745',
                                                        border: 'none',
                                                        borderRadius: '5px',
                                                        cursor: 'pointer',
                                                    }}
                                                >
                                                    Log In
                                                </button>
                                            </form>
                                        </div>
                                    </div>
                                </div>
                            )
                        }
                    />
                    <Route
                        path="/products"
                        element={isAuthenticated ? <ProductManagement products={products} setProducts={setProducts} /> : <Navigate to="/auth" />}
                    />
                    <Route
                        path="/dashboard"
                        element={isAuthenticated ? <Dashboard products={products} onLogout={handleLogout} /> : <Navigate to="/auth" />}
                    />
                    <Route path="/" element={<Navigate to="/auth" />} />
                </Routes>
            </div>
        </Router>
    );
};

export default App;
