import React from 'react';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';

const NewDashboard = ({ products }) => {
    return (
        <div style={styles.dashboardContainer}>
            <header style={styles.header}>
                <h1 style={styles.title}>Dashboard Analytics</h1>
            </header>
            <main style={styles.main}>
                <div style={styles.chartContainer}>
                    <ResponsiveContainer width="100%" height={400}>
                        <BarChart data={products} margin={styles.chartMargins}>
                            <CartesianGrid strokeDasharray="5 5" stroke="#444" />
                            <XAxis dataKey="name" stroke="#ccc" />
                            <YAxis stroke="#ccc" />
                            <Tooltip contentStyle={styles.tooltip} itemStyle={styles.tooltipText} />
                            <Legend wrapperStyle={styles.legend} />
                            <Bar dataKey="quantity" fill="#E91E63" name="Quantity" radius={[5, 5, 0, 0]} />
                        </BarChart>
                    </ResponsiveContainer>
                </div>
            </main>
        </div>
    );
};

const styles = {
    dashboardContainer: {
        width: '95%',
        maxWidth: '1100px',
        margin: '30px auto',
        padding: '0',
        backgroundColor: '#1E1E2F',
        borderRadius: '10px',
        overflow: 'hidden',
        fontFamily: "'Roboto', sans-serif",
        color: '#fff',
        boxShadow: '0 10px 30px rgba(0, 0, 0, 0.3)',
    },
    header: {
        padding: '20px',
        backgroundColor: '#2C2C45',
        textAlign: 'center',
        borderBottom: '2px solid #444',
    },
    title: {
        fontSize: '2.5em',
        color: '#F8F9FA',
        margin: '0',
    },
    main: {
        padding: '20px',
    },
    chartContainer: {
        backgroundColor: '#252540',
        padding: '20px',
        borderRadius: '8px',
        boxShadow: 'inset 0 0 10px rgba(0, 0, 0, 0.5)',
    },
    chartMargins: {
        top: 20,
        right: 30,
        left: 20,
        bottom: 5,
    },
    tooltip: {
        backgroundColor: '#333',
        border: '1px solid #555',
        borderRadius: '5px',
        color: '#fff',
        padding: '10px',
    },
    tooltipText: {
        color: '#F8F9FA',
    },
    legend: {
        fontSize: '0.9em',
        color: '#ccc',
        paddingTop: '10px',
    },
};

export default NewDashboard;
