import React, { useState, useEffect } from 'react';
import axios from 'axios';

const ProductManagement = () => {
  const [products, setProducts] = useState([]);
  const [newProduct, setNewProduct] = useState({
    name: '',
    description: '',
    category: '',
    price: '',
    quantity: ''
  });

  // Fetch products from the API
  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/products');
        setProducts(response.data);
      } catch (error) {
        console.error('Error fetching products:', error);
      }
    };
    
    fetchProducts();
  }, []);

  // Add product to both local state and database
  const addProduct = async (e) => {
    e.preventDefault();
    
    try {
      const response = await axios.post('http://localhost:5000/api/products', newProduct);
      // Update local state with the new product including the ID from the response
      setProducts([...products, { ...newProduct, id: response.data.id }]);
      // Reset form fields
      setNewProduct({ name: '', description: '', category: '', price: '', quantity: '' });
    } catch (error) {
      console.error('Error adding product:', error);
    }
  };

  return (
    <div>
      <h2>Product Management</h2>
      <form onSubmit={addProduct}>
        <input 
          type="text" 
          placeholder="Name" 
          value={newProduct.name} 
          onChange={e => setNewProduct({ ...newProduct, name: e.target.value })} 
          required 
        />
        <input 
          type="text" 
          placeholder="Description" 
          value={newProduct.description} 
          onChange={e => setNewProduct({ ...newProduct, description: e.target.value })} 
          required 
        />
        <input 
          type="text" 
          placeholder="Category" 
          value={newProduct.category} 
          onChange={e => setNewProduct({ ...newProduct, category: e.target.value })} 
          required 
        />
        <input 
          type="number" 
          placeholder="Price" 
          value={newProduct.price} 
          onChange={e => setNewProduct({ ...newProduct, price: e.target.value })} 
          required 
        />
        <input 
          type="number" 
          placeholder="Quantity" 
          value={newProduct.quantity} 
          onChange={e => setNewProduct({ ...newProduct, quantity: e.target.value })} 
          required 
        />
        <button type="submit">Add Product</button>
      </form>
      <ul>
        {products.map(product => (
          <li key={product.id}>
            {product.name} - {product.description} - ${product.price.toFixed(2)} - Quantity: {product.quantity}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default ProductManagement;
