// components/ProductManagement.js
import React, { useState } from 'react';

const ProductManagement = ({ products, setProducts }) => {
    const [formData, setFormData] = useState({ id: null, name: '', category: '', quantity: 0, price: 0 });

    const handleSubmit = (e) => {
        e.preventDefault();
        if (formData.id !== null) {
            const updatedProducts = products.map((product) =>
                product.id === formData.id ? formData : product
            );
            setProducts(updatedProducts);
        } else {
            setProducts([...products, { ...formData, id: Date.now() }]);
        }
        setFormData({ id: null, name: '', category: '', quantity: 0, price: 0 });
    };

    const handleDelete = (id) => {
        setProducts(products.filter((product) => product.id !== id));
    };

    return (
        <div style={{ marginBottom: '20px', width: '80%' }}>
            <h2>Product Management</h2>
            <form onSubmit={handleSubmit} style={{ marginTop: '10px' }}>
                <input
                    type="text"
                    placeholder="Name"
                    value={formData.name}
                    onChange={(e) => setFormData({ ...formData, name: e.target.value })}
                    required
                    style={{ marginBottom: '5px' }}
                />
                <input
                    type="text"
                    placeholder="Category"
                    value={formData.category}
                    onChange={(e) => setFormData({ ...formData, category: e.target.value })}
                    required
                    style={{ marginBottom: '5px' }}
                />
                <input
                    type="number"
                    placeholder="Quantity"
                    value={formData.quantity}
                    onChange={(e) => setFormData({ ...formData, quantity: parseInt(e.target.value) || 0 })}
                    required
                    style={{ marginBottom: '5px' }}
                />
                <input
                    type="number"
                    placeholder="Price"
                    value={formData.price}
                    onChange={(e) => setFormData({ ...formData, price: parseFloat(e.target.value) || 0 })}
                    required
                    style={{ marginBottom: '5px' }}
                />
                <button type="submit" style={{ marginLeft: '5px', cursor: 'pointer' }}>
                    {formData.id ? 'Update' : 'Add'} Product
                </button>
            </form>

            <ul style={{ marginTop: '10px' }}>
                {products.map((product) => (
                    <li key={product.id}>
                        {product.name} - {product.category} - {product.quantity} in stock
                        <button onClick={() => setFormData(product)} style={{ marginLeft: '5px', cursor: 'pointer' }}>Edit</button>
                        <button onClick={() => handleDelete(product.id)} style={{ marginLeft: '5px', cursor: 'pointer' }}>Delete</button>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default ProductManagement;
