import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate, Link } from 'react-router-dom';
import Dashboard from './components/Dashboard';
import ProductManagement from './components/ProductManagement';
import UserManagement2 from './components/UserManagement2';
import SignUp from './components/SignUp';
import Login from './components/Login'; // New Login component
import './App.css'; // Main CSS

const App = () => {
    const [isAuthenticated, setIsAuthenticated] = useState(false);
    const [products, setProducts] = useState([
        { id: 1, name: 'Chicken Wings', category: 'Food', quantity: 50, price: 10 },
        { id: 2, name: 'Soda', category: 'Beverage', quantity: 100, price: 1.5 },
    ]);
    const [users, setUsers] = useState([]);

    const handleLogout = () => setIsAuthenticated(false);

    return (
        <Router>
            <marquee className="marquee">Limkokwing Wings Cafe</marquee>
            <nav className="nav">
                {isAuthenticated ? (
                    <>
                        <Link to="/dashboard" className="navLink">Dashboard</Link>
                        <Link to="/products" className="navLink">Product Management</Link>
                        <Link to="/users2" className="navLink">User Management</Link>
                        <Link to="/login" className="navLink" onClick={handleLogout}>Logout</Link>
                    </>
                ) : (
                    <Link to="/login" className="navLink">Login</Link>
                )}
            </nav>

            <Routes>
                <Route path="/login" element={<Login setIsAuthenticated={setIsAuthenticated} />} />
                <Route path="/signup" element={<SignUp />} />
                <Route path="/products" element={isAuthenticated ? <ProductManagement products={products} setProducts={setProducts} /> : <Navigate to="/login" />} />
                <Route path="/dashboard" element={isAuthenticated ? <Dashboard products={products} /> : <Navigate to="/login" />} />
                <Route path="/users2" element={isAuthenticated ? <userManagement2 users={users} setUsers={setUsers} /> : <Navigate to="/login" />} />
                <Route path="/users2" element={isAuthenticated ? <UserManagement users={users} setUsers={setUsers} /> : <Navigate to="/login" />} />
                <Route path="/" element={<Navigate to="/login" />} />
            </Routes>
        </Router>
    );
};

export default App;
