import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate, Link } from 'react-router-dom';
import Dashboard from './Dashboard';
import ProductManagement from './ProductManagement';
import UserManagement from './UserManagement';

const App = () => {
    const [isAuthenticated, setIsAuthenticated] = useState(false);
    const [products, setProducts] = useState([
        { id: 1, name: 'Chicken Wings', category: 'Food', quantity: 50, price: 10 },
        { id: 2, name: 'Soda', category: 'Beverage', quantity: 100, price: 1.5 },
    ]);

    const handleLogin = () => {
        setIsAuthenticated(true);
    };

    const handleLogout = () => {
        setIsAuthenticated(false);
    };

    const navStyles = {
        container: { background: '#333', padding: '1rem', display: 'flex', justifyContent: 'center' },
        link: { color: '#fff', textDecoration: 'none', margin: '0 1rem', padding: '0.5rem 1rem', borderRadius: '4px' },
        activeLink: { background: '#555' },
        logoutButton: { color: '#fff', border: 'none', background: 'none', cursor: 'pointer', margin: '0 1rem', padding: '0.5rem 1rem' }
    };

    return (
        <Router>
            <div>
                <nav style={navStyles.container}>
                    {isAuthenticated && (
                        <>
                            <Link to="/dashboard" style={{ ...navStyles.link, ...navStyles.activeLink }}>Dashboard</Link>
                            <Link to="/products" style={navStyles.link}>Product Management</Link>
                            <button onClick={handleLogout} style={navStyles.logoutButton}>Logout</button>
                        </>
                    )}
                </nav>

                <Routes>
                    <Route path="/login" element={isAuthenticated ? <Navigate to="/products" /> : <UserManagement onLogin={handleLogin} />} />
                    <Route path="/products" element={isAuthenticated ? <ProductManagement products={products} setProducts={setProducts} /> : <Navigate to="/login" />} />
                    <Route path="/dashboard" element={isAuthenticated ? <Dashboard products={products} onLogout={handleLogout} /> : <Navigate to="/login" />} />
                    <Route path="/" element={<Navigate to="/login" />} />
                </Routes>
            </div>
        </Router>
    );
};

export default App;
