import React, { useState } from 'react';
import './App.css';

// Dashboard Component
const Dashboard = ({ products, onLogout }) => {
    const totalProducts = products.length;
    const totalStock = products.reduce((sum, product) => sum + product.quantity, 0);

    return (
        <div className="dashboard">
            <h1>Dashboard</h1>
            <button onClick={onLogout} className="form-button logout-button">Logout</button>
            <div className="dashboard-stats">
                <div>Total Products: {totalProducts}</div>
                <div>Total Stock: {totalStock}</div>
            </div>

            <h2>Stock Overview</h2>
            <table className="styled-table">
                <thead>
                    <tr>
                        <th>Product</th>
                        <th>Category</th>
                        <th>Stock</th>
                    </tr>
                </thead>
                <tbody>
                    {products.map((product) => (
                        <tr key={product.id}>
                            <td>{product.name}</td>
                            <td>{product.category}</td>
                            <td>{product.quantity}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

// Product Management Component
const ProductManagement = ({ products, setProducts }) => {
    const [formData, setFormData] = useState({ name: '', category: '', quantity: 0, price: 0 });

    const handleSubmit = (e) => {
        e.preventDefault();
        if (formData.id) {
            const updatedProducts = products.map((product) =>
                product.id === formData.id ? formData : product
            );
            setProducts(updatedProducts);
        } else {
            setProducts([...products, { ...formData, id: Date.now() }]);
        }
        setFormData({ name: '', category: '', quantity: 0, price: 0 });
    };

    const handleDelete = (id) => {
        setProducts(products.filter((product) => product.id !== id));
    };

    return (
        <div className="product-management">
            <h2>Product Management</h2>
            <form onSubmit={handleSubmit} className="styled-form">
                <input
                    type="text"
                    placeholder="Name"
                    value={formData.name}
                    onChange={(e) => setFormData({ ...formData, name: e.target.value })}
                    required
                />
                <input
                    type="text"
                    placeholder="Category"
                    value={formData.category}
                    onChange={(e) => setFormData({ ...formData, category: e.target.value })}
                    required
                />
                <input
                    type="number"
                    placeholder="Quantity"
                    value={formData.quantity}
                    onChange={(e) => setFormData({ ...formData, quantity: parseInt(e.target.value) })}
                    required
                />
                <input
                    type="number"
                    placeholder="Price"
                    value={formData.price}
                    onChange={(e) => setFormData({ ...formData, price: parseFloat(e.target.value) })}
                    required
                />
                <button type="submit" className="form-button">
                    {formData.id ? 'Update' : 'Add'} Product
                </button>
            </form>

            <ul className="product-list">
                {products.map((product) => (
                    <li key={product.id}>
                        {product.name} - {product.category} - {product.quantity} in stock
                        <button onClick={() => setFormData(product)} className="edit-button">Edit</button>
                        <button onClick={() => handleDelete(product.id)} className="delete-button">Delete</button>
                    </li>
                ))}
            </ul>
        </div>
    );
};

// User Management (Login) Component
const UserManagement = ({ onLogin }) => {
    const [loginData, setLoginData] = useState({ username: '', email: '' });

    const handleLogin = (e) => {
        e.preventDefault();
        onLogin(); // Login without checking credentials
        setLoginData({ username: '', email: '' });
    };

    return (
        <div className="user-management">
            <h1 className="app-heading">Limkokwing Wings Cafe </h1>
            <h2>User Login</h2>
            <form onSubmit={handleLogin} className="styled-form">
                <input
                    type="text"
                    placeholder="Username"
                    value={loginData.username}
                    onChange={(e) => setLoginData({ ...loginData, username: e.target.value })}
                    required
                />
                <input
                    type="email"
                    placeholder="Email"
                    value={loginData.email}
                    onChange={(e) => setLoginData({ ...loginData, email: e.target.value })}
                    required
                />
                <button type="submit" className="form-button">Login</button>
            </form>
        </div>
    );
};

// Main App Component
const App = () => {
    const [view, setView] = useState('login');
    const [products, setProducts] = useState([
        { id: 1, name: 'Chicken Wings', category: 'Food', quantity: 50, price: 10 },
        { id: 2, name: 'Soda', category: 'Beverage', quantity: 100, price: 1.5 },
    ]);

    const handleLogin = () => {
        setView('products'); // Redirect to Product Management after login
    };

    const handleLogout = () => {
        setView('login'); // Redirect back to login on logout
    };

    return (
        <div>
            <nav className="navigation">
                {view !== 'login' && (
                    <>
                        <button onClick={() => setView('dashboard')} className="nav-button">Dashboard</button>
                        <button onClick={() => setView('products')} className="nav-button">Product Management</button>
                        <button onClick={handleLogout} className="nav-button">Logout</button>
                    </>
                )}
            </nav>

            {view === 'login' && <UserManagement onLogin={handleLogin} />}
            {view === 'products' && <ProductManagement products={products} setProducts={setProducts} />}
            {view === 'dashboard' && <Dashboard products={products} onLogout={handleLogout} />}
        </div>
    );
};

export default App;
