import React, { useState } from 'react';

const ProductManagement = ({ products, setProducts }) => {
    const [formData, setFormData] = useState({ name: '', category: '', quantity: 0, price: 0 });

    const handleSubmit = (e) => {
        e.preventDefault();
        if (formData.id) {
            setProducts(products.map((product) => (product.id === formData.id ? formData : product)));
        } else {
            setProducts([...products, { ...formData, id: Date.now() }]);
        }
        setFormData({ name: '', category: '', quantity: 0, price: 0 });
    };

    const handleDelete = (id) => setProducts(products.filter((product) => product.id !== id));

    const styles = {
        container: { padding: '1.5rem', maxWidth: '600px', margin: '2rem auto', background: '#f9f9f9', borderRadius: '8px', boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)' },
        input: { margin: '0.5rem 0', padding: '0.6rem', fontSize: '1rem', width: '100%', borderRadius: '5px', border: '1px solid #ccc' },
        button: { margin: '0.5rem', padding: '0.5rem 1rem', borderRadius: '5px', border: 'none', cursor: 'pointer' },
        submitButton: { background: '#28a745', color: '#fff' },
        editButton: { background: '#007bff', color: '#fff' },
        deleteButton: { background: '#dc3545', color: '#fff' },
        listItem: { padding: '0.5rem 0', borderBottom: '1px solid #eee' }
    };

    return (
        <div style={styles.container}>
            <h2>Product Management</h2>
            <form onSubmit={handleSubmit}>
                <input
                    type="text"
                    placeholder="Name"
                    value={formData.name}
                    onChange={(e) => setFormData({ ...formData, name: e.target.value })}
                    required
                    style={styles.input}
                />
                <input
                    type="text"
                    placeholder="Category"
                    value={formData.category}
                    onChange={(e) => setFormData({ ...formData, category: e.target.value })}
                    required
                    style={styles.input}
                />
                <input
                    type="number"
                    placeholder="Quantity"
                    value={formData.quantity}
                    onChange={(e) => setFormData({ ...formData, quantity: parseInt(e.target.value) })}
                    required
                    style={styles.input}
                />
                <input
                    type="number"
                    placeholder="Price"
                    value={formData.price}
                    onChange={(e) => setFormData({ ...formData, price: parseFloat(e.target.value) })}
                    required
                    style={styles.input}
                />
                <button type="submit" style={{ ...styles.button, ...styles.submitButton }}>
                    {formData.id ? 'Update' : 'Add'} Product
                </button>
            </form>

            <ul>
                {products.map((product) => (
                    <li key={product.id} style={styles.listItem}>
                        {product.name} - {product.category} - {product.quantity} in stock
                        <button onClick={() => setFormData(product)} style={{ ...styles.button, ...styles.editButton }}>Edit</button>
                        <button onClick={() => handleDelete(product.id)} style={{ ...styles.button, ...styles.deleteButton }}>Delete</button>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default ProductManagement;
