import React from 'react';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';

const Dashboard = ({ products }) => {
    return (
        <div style={styles.dashboardContainer}>
            <h2 style={styles.title}>Product Overview</h2>
            <div style={styles.chartContainer}>
                <ResponsiveContainer width="100%" height={400}>
                    <BarChart data={products} margin={styles.chartMargins}>
                        <CartesianGrid strokeDasharray="3 3" stroke="#ccc" />
                        <XAxis dataKey="name" stroke="#555" />
                        <YAxis stroke="#555" />
                        <Tooltip contentStyle={styles.tooltip} itemStyle={styles.tooltipText} />
                        <Legend wrapperStyle={styles.legend} />
                        <Bar dataKey="quantity" fill="#5A9BFF" name="Quantity" radius={[10, 10, 0, 0]} />
                    </BarChart>
                </ResponsiveContainer>
            </div>
        </div>
    );
};

const styles = {
    dashboardContainer: {
        width: '90%',
        maxWidth: '1200px',
        margin: '40px auto',
        padding: '30px',
        backgroundColor: '#f9f9f9',
        borderRadius: '12px',
        boxShadow: '0 8px 20px rgba(0, 0, 0, 0.1)',
        fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
    },
    title: {
        fontSize: '2em',
        color: '#333',
        textAlign: 'center',
        marginBottom: '20px',
    },
    chartContainer: {
        width: '100%',
        overflowX: 'auto', // To handle horizontal overflow on small screens
    },
    chartMargins: {
        top: 20,
        right: 30,
        left: 20,
        bottom: 5,
    },
    tooltip: {
        backgroundColor: '#fff',
        border: '1px solid #ddd',
        borderRadius: '8px',
        padding: '10px',
        boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
    },
    tooltipText: {
        color: '#555',
    },
    legend: {
        fontSize: '0.9em',
        color: '#555',
        paddingTop: '10px',
    },
};

export default Dashboard;
