import React, { useState } from 'react';

const UserManagement = ({ users, setUsers }) => {
    const [formData, setFormData] = useState({ id: '', username: '', email: '' });

    const handleSubmit = (e) => {
        e.preventDefault();
        if (formData.id) {
            const updatedUsers = users.map((user) =>
                user.id === formData.id ? formData : user
            );
            setUsers(updatedUsers);
        } else {
            setUsers([...users, { ...formData, id: Date.now() }]);
        }
        setFormData({ id: '', username: '', email: '' }); // Reset form
    };

    const handleDelete = (id) => {
        setUsers(users.filter((user) => user.id !== id));
    };

    return (
        <div style={styles.container}>
            <h2 style={styles.heading}>User Management</h2>
            <form onSubmit={handleSubmit} style={styles.form}>
                <input
                    type="text"
                    placeholder="Username"
                    value={formData.username}
                    onChange={(e) => setFormData({ ...formData, username: e.target.value })}
                    required
                    style={styles.input}
                />
                <input
                    type="email"
                    placeholder="Email"
                    value={formData.email}
                    onChange={(e) => setFormData({ ...formData, email: e.target.value })}
                    required
                    style={styles.input}
                />
                <button type="submit" style={styles.button}>
                    {formData.id ? 'Update' : 'Add'} User
                </button>
            </form>

            <ul style={styles.list}>
                {users.map((user) => (
                    <li key={user.id} style={styles.listItem}>
                        {user.username} - {user.email}
                        <button onClick={() => setFormData(user)} style={styles.editButton}>Edit</button>
                        <button onClick={() => handleDelete(user.id)} style={styles.deleteButton}>Delete</button>
                    </li>
                ))}
            </ul>
        </div>
    );
};

const styles = {
    container: {
        maxWidth: '600px',
        margin: '2rem auto',
        padding: '2rem',
        backgroundColor: '#f5f5f5',
        borderRadius: '8px',
        boxShadow: '0px 4px 8px rgba(0, 0, 0, 0.1)',
        textAlign: 'center',
        fontFamily: 'Arial, sans-serif',
    },
    heading: {
        color: '#333',
        fontSize: '1.8rem',
        marginBottom: '1.5rem',
    },
    form: {
        display: 'flex',
        flexDirection: 'column',
        gap: '1rem',
        marginBottom: '2rem',
    },
    input: {
        padding: '0.8rem',
        fontSize: '1rem',
        border: '1px solid #ddd',
        borderRadius: '4px',
        transition: 'border-color 0.2s',
        width: '200px',
        alignSelf: 'center',
    },
    button: {
        padding: '0.8rem',
        fontSize: '1rem',
        color: '#fff',
        backgroundColor: '#2193b0',
        border: 'none',
        borderRadius: '4px',
        cursor: 'pointer',
        transition: 'background-color 0.3s',
        alignSelf: 'center',
    },
    list: {
        listStyleType: 'none',
        padding: 0,
        margin: 0,
    },
    listItem: {
        padding: '1rem',
        margin: '0.5rem 0',
        backgroundColor: '#fff',
        borderRadius: '4px',
        boxShadow: '0px 2px 4px rgba(0, 0, 0, 0.1)',
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center',
    },
    editButton: {
        padding: '0.5rem',
        fontSize: '0.9rem',
        color: '#fff',
        backgroundColor: '#ffb74d',
        border: 'none',
        borderRadius: '4px',
        cursor: 'pointer',
        transition: 'background-color 0.3s',
        marginLeft: '0.5rem',
    },
    deleteButton: {
        padding: '0.5rem',
        fontSize: '0.9rem',
        color: '#fff',
        backgroundColor: '#f44336',
        border: 'none',
        borderRadius: '4px',
        cursor: 'pointer',
        transition: 'background-color 0.3s',
        marginLeft: '0.5rem',
    },
};

export default UserManagement;
