import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate, Link } from 'react-router-dom';
import Dashboard from './components/Dashboard';
import ProductManagement from './components/ProductManagement';
import UserManagement2 from './components/UserManagement2'; // Corrected to PascalCase
import SignUp from './components/SignUp';
import './App.css'; // Ensure this file exists

const App = () => {
    const [isAuthenticated, setIsAuthenticated] = useState(false);
    const [products, setProducts] = useState([
        { id: 1, name: 'Chicken Wings', category: 'Food', quantity: 50, price: 10 },
        { id: 2, name: 'Soda', category: 'Beverage', quantity: 100, price: 1.5 },
    ]);
    const [users, setUsers] = useState([]);

    const handleLogin = () => setIsAuthenticated(true);
    const handleLogout = () => setIsAuthenticated(false);

    return (
        <Router>

            <Routes>
                <Route
                    path="/login"
                    element={isAuthenticated ? <Navigate to="/dashboard" /> : (
                        <div className="loginContainer">
                            <h2 className="title">Login</h2>
                            <form
                                onSubmit={(e) => {
                                    e.preventDefault();
                                    handleLogin();
                                }}
                                className="form"
                            >
                                <input type="text" placeholder="Username" required className="input" />
                                <input type="password" placeholder="Password" required className="input" />
                                <button type="submit" className="button">Login</button>
                            </form>
                            <Link to="/signup" className="signUpLink">Sign Up</Link>
                        </div>
                    )}
                />
                <Route path="/signup" element={<SignUp />} />
                <Route path="/products" element={isAuthenticated ? <ProductManagement products={products} setProducts={setProducts} /> : <Navigate to="/login" />} />
                <Route path="/dashboard" element={isAuthenticated ? <Dashboard products={products} /> : <Navigate to="/login" />} />
                <Route path="/users2" element={isAuthenticated ? <UserManagement2 users={users} setUsers={setUsers} /> : <Navigate to="/login" />} />
                <Route path="/" element={<Navigate to="/login" />} />
            </Routes>
        </Router>
    );
};

export default App;
