import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './SignUp.css';  // Ensure the CSS file path is correct

const SignUp = () => {
    const [isSignedUp, setIsSignedUp] = useState(false);  // State to manage sign-up process

    // Handler for the sign-up form submission
    const handleSignUp = (e) => {
        e.preventDefault();  // Prevent form from reloading the page
        setIsSignedUp(true);  // Show success message after form submission
    };

    return (
        <div className="signup-container">
            <div className="signup-box">
                <h2>Create New Account</h2>
                {/* Displaying the form or success message depending on the state */}
                {!isSignedUp ? (
                    <form onSubmit={handleSignUp}>
                        <input type="text" placeholder="Full Name" required />
                        <input type="email" placeholder="Email" required />
                        <input type="password" placeholder="Password" required />
                        <button type="submit" className="signup-btn">
                            Sign Up
                        </button>
                    </form>
                ) : (
                    <div className="success-message">
                        <p>Registration successful! You can now log in.</p>
                        <Link to="/login" className="login-link">Go to Login</Link>
                    </div>
                )}
            </div>
        </div>
    );
};

export default SignUp;
