import React, { useState } from 'react';

const UserManagement = ({ onLogin }) => {
    const [loginData, setLoginData] = useState({ username: '', email: '' });

    const handleLogin = (e) => {
        e.preventDefault();
        onLogin();
        setLoginData({ username: '', email: '' });
    };

    // Inline styling objects
    const containerStyle = {
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        minHeight: '100vh',
        backgroundColor: '#f2f2f2',
    };

    const boxStyle = {
        backgroundColor: '#ffffff',
        borderRadius: '8px',
        boxShadow: '0 4px 8px rgba(0, 0, 0, 0.2)',
        padding: '2rem',
        width: '300px',
        textAlign: 'center',
    };

    const headingStyle = {
        fontSize: '1.5rem',
        fontWeight: 'bold',
        color: '#333333',
        marginBottom: '1rem',
    };

    const formStyle = {
        display: 'flex',
        flexDirection: 'column',
    };

    const inputStyle = {
        marginBottom: '1rem',
        padding: '0.8rem',
        fontSize: '1rem',
        border: '1px solid #cccccc',
        borderRadius: '4px',
    };

    const buttonStyle = {
        backgroundColor: '#4caf50',
        color: '#ffffff',
        border: 'none',
        borderRadius: '4px',
        padding: '0.8rem',
        fontSize: '1rem',
        cursor: 'pointer',
        transition: 'background-color 0.3s',
    };

    const buttonHoverStyle = {
        backgroundColor: '#45a049',
    };

    return (
        <div style={containerStyle}>
            <div style={boxStyle}>
                <h1 style={headingStyle}>Limkokwing Wings Cafe</h1>
                <h2>User Login</h2>
                <form onSubmit={handleLogin} style={formStyle}>
                    <input
                        type="text"
                        placeholder="Username"
                        value={loginData.username}
                        onChange={(e) => setLoginData({ ...loginData, username: e.target.value })}
                        required
                        style={inputStyle}
                    />
                    <input
                        type="email"
                        placeholder="Email"
                        value={loginData.email}
                        onChange={(e) => setLoginData({ ...loginData, email: e.target.value })}
                        required
                        style={inputStyle}
                    />
                    <button
                        type="submit"
                        style={{
                            ...buttonStyle,
                            ':hover': buttonHoverStyle,
                        }}
                    >
                        Login
                    </button>
                </form>
            </div>
        </div>
    );
};

export default UserManagement;
