import React, { useState } from 'react';
import '../App.css';

const UserManagement = ({ onLogin }) => {
    const [loginData, setLoginData] = useState({ username: '', email: '' });

    const handleLogin = (e) => {
        e.preventDefault();
        onLogin(); // Login without checking credentials
        setLoginData({ username: '', email: '' });
    };

    return (
        <div className="user-management">
            <h1 className="app-heading">Limkokwing Wings Cafe </h1>
            <h2>User Login</h2>
            <form onSubmit={handleLogin} className="styled-form">
                <input
                    type="text"
                    placeholder="Username"
                    value={loginData.username}
                    onChange={(e) => setLoginData({ ...loginData, username: e.target.value })}
                    required
                />
                <input
                    type="email"
                    placeholder="Email"
                    value={loginData.email}
                    onChange={(e) => setLoginData({ ...loginData, email: e.target.value })}
                    required
                />
                <button type="submit" className="form-button">Login</button>
            </form>
        </div>
    );
};

export default UserManagement;
