import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../App.css';

const ProductManagement = () => {
    const [products, setProducts] = useState([]);
    const [formData, setFormData] = useState({ id: null, name: '', category: '', quantity: 0, price: 0 });

    // Fetch products from the database on component mount
    useEffect(() => {
        fetchProducts();
    }, []);

    const fetchProducts = async () => {
        try {
            const response = await axios.get('http://localhost:5001/api/products');
            setProducts(response.data);
        } catch (error) {
            console.error('Error fetching products:', error);
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            let response;
            if (formData.id !== null) {
                response = await axios.put(`http://localhost:5001/api/products/${formData.id}`, formData);
            } else {
                response = await axios.post('http://localhost:5001/api/products', { ...formData });
            }
            console.log('Response from server:', response.data); // Log server response
            fetchProducts(); // Fetch updated products after adding/updating
            resetForm();
        } catch (error) {
            console.error('Error saving product:', error);
        }
    };

    const resetForm = () => {
        setFormData({ id: null, name: '', category: '', quantity: 0, price: 0 });
    };

    const handleDelete = async (id) => {
        try {
            await axios.delete(`http://localhost:5001/api/products/${id}`);
            fetchProducts(); // Refresh the list after deletion
        } catch (error) {
            console.error('Error deleting product:', error);
        }
    };

    return (
        <div className="product-management">
            <h2>Product Management</h2>
            <form onSubmit={handleSubmit} className="styled-form">
                <input type="text" placeholder="Name" value={formData.name} onChange={(e) => setFormData({ ...formData, name: e.target.value })} required />
                <input type="text" placeholder="Category" value={formData.category} onChange={(e) => setFormData({ ...formData, category: e.target.value })} required />
                <input type="number" placeholder="Quantity" value={formData.quantity} onChange={(e) => setFormData({ ...formData, quantity: parseInt(e.target.value) || 0 })} required />
                <input type="number" placeholder="Price" value={formData.price} onChange={(e) => setFormData({ ...formData, price: parseFloat(e.target.value) || 0 })} required />
                <button type="submit" className="form-button">{formData.id ? 'Update' : 'Add'} Product</button>
            </form>

            <ul className="product-list">
                {products.map((product) => (
                    <li key={product.id}>
                        {product.name} - {product.category} - {product.quantity} in stock - ${parseFloat(product.price).toFixed(2)}
                        <button onClick={() => setFormData(product)} className="edit-button">Edit</button>
                        <button onClick={() => handleDelete(product.id)} className="delete-button">Delete</button>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default ProductManagement;
