// SignUp.js
import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const SignUp = () => {
    const [isSignedUp, setIsSignedUp] = useState(false);

    const handleSignUp = (e) => {
        e.preventDefault();
        setIsSignedUp(true); // Show confirmation message
    };

    return (
        <div style={{
            textAlign: 'center',
            margin: '2rem auto',
            padding: '2rem',
            maxWidth: '400px',
            borderRadius: '8px',
            background: '#ff4b1f',
            color: '#fff'
        }}>
            <h2>Sign Up</h2>
            {!isSignedUp ? (
                <form onSubmit={handleSignUp}>
                    <input type="text" placeholder="Username" required style={{ margin: '0.5rem 0', padding: '0.5rem', width: '100%' }} />
                    <input type="password" placeholder="Password" required style={{ margin: '0.5rem 0', padding: '0.5rem', width: '100%' }} />
                    <input type="email" placeholder="Email" required style={{ margin: '0.5rem 0', padding: '0.5rem', width: '100%' }} />
                    <button type="submit" style={{ padding: '0.5rem 1rem', background: '#2193b0', color: '#fff', border: 'none', borderRadius: '4px', cursor: 'pointer' }}>
                        Register
                    </button>
                </form>
            ) : (
                <div>
                    <p>Registration successful! You can now log in.</p>
                    <Link to="/login" style={{ color: '#fff', textDecoration: 'none' }}>Go to Login</Link>
                </div>
            )}
        </div>
    );
};

export default SignUp;
